import React, { useContext } from 'react';
import './Cart.css';
import { useCart } from '../../MyContext/CartContext';

const Cart = (props) => {
  const { state, dispatch } = useCart();

  const removeItem = (itemId) => {
    dispatch({ type: 'REMOVE_FROM_CART', payload: itemId });
    removeFromCartAPI(itemId); // Call API to remove item
  };

  const removeFromCartAPI = (itemId) => {
    fetch(`https://crudcrud.com/api/2c44a8fd9efd43298949ffc93552d80e/cart/${itemId}`, {
      method: 'DELETE',
    })
      .then((response) => response.json())
      .then((data) => console.log('Item removed from cart:', data))
      .catch((error) => console.error('Error removing item from cart:', error));
  };

  return (
    <div className="cart">
      <h2>Your Cart</h2>
      {state.items.length === 0 ? (
        <p>Your cart is empty</p>
      ) : (
        state.items.map((item) => (
          <div key={item.id} className="cart-item">
            <img src={item.imageUrl} alt={item.title} className="cart-item-img" />
            <div className="cart-item-details">
              <div>{item.title}</div>
              <div>${item.price} x {item.quantity}</div>
              <button className="remove-item-button" onClick={() => removeItem(item.id)}>Remove</button>
            </div>
          </div>
        ))
      )}
      <button onClick={props.onClick} className='close-button'>X</button>
    </div>
  );
};

export default Cart;
