import React, { useContext } from 'react';
import './Cart.css';
import { useCart  , removeFromCartAPI} from '../../MyContext/CartContext';
import AuthContext from '../../MyContext/auth-context';


const Cart = (props) => {
  const { state, dispatch } = useCart();
  const authCtx = useContext(AuthContext);

  const removeItem = (itemId) => {
    dispatch({ type: 'REMOVE_FROM_CART', payload: itemId });
    removeFromCartAPI(itemId); // Call API to remove item
  };

  return (
   authCtx.isLoggedIn && ( <div className="cart">
      <h2>Your Cart</h2>
      {state.items.length === 0 ? (
        <p>Your cart is empty</p>
      ) : (
        state.items.map((item) => (
          <div key={item._id} className="cart-item">
            <img src={item.imageUrl} alt={item.title} className="cart-item-img" />
            <div className="cart-item-details">
              <div>{item.title}</div>
              <div>${item.price} x {item.quantity}</div>
              <button className="remove-item-button" onClick={() => removeItem(item._id)}>Remove</button>
            </div>
          </div>
        ))
      )}
      <button onClick={props.onClick} className='close-button'>X</button>
    </div>)
  );
};

export default Cart;
