import React, { createContext, useReducer, useContext } from 'react';

const CartContext = createContext();

const cartReducer = (state, action) => {
  switch (action.type) {
    case 'ADD_TO_CART':
      addToCartAPI(action.payload); // Call API to add item
      return state;
    case 'REMOVE_FROM_CART':
      removeFromCartAPI(action.payload); // Call API to remove item
      return state;
    case 'FETCH_CART_ITEMS':
      fetchCartItemsAPI(); // Call API to fetch cart items
      return state;
    default:
      return state;
  }
};

const baseURL = 'https://crudcrud.com/api/2c44a8fd9efd43298949ffc93552d80e';

const addToCartAPI = (item) => {
  fetch(`${baseURL}/cart`, {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify(item),
  })
    .then((response) => response.json())
    .then((data) => console.log('Item added to cart:', data))
    .catch((error) => console.error('Error adding item to cart:', error));
};

const removeFromCartAPI = (itemId) => {
  fetch(`${baseURL}/cart/${itemId}`, {
    method: 'DELETE',
  })
    .then((response) => response.json())
    .then((data) => console.log('Item removed from cart:', data))
    .catch((error) => console.error('Error removing item from cart:', error));
};

const fetchCartItemsAPI = () => {
  fetch(`${baseURL}/cart`)
    .then((response) => response.json())
    .then((data) => console.log('Cart items fetched:', data))
    .catch((error) => console.error('Error fetching cart items:', error));
};

const initialCartState = {
  items: [],
};

export const CartProvider = ({ children }) => {
  const [state, dispatch] = useReducer(cartReducer, initialCartState);

  return (
    <CartContext.Provider value={{ state, dispatch }}>
      {children}
    </CartContext.Provider>
  );
};

export const useCart = () => useContext(CartContext);
