import React from 'react';
import './Product.css';
import { useCart } from '../../MyContext/CartContext';

const Product = ({ title, price, imageUrl }) => {
  const { dispatch } = useCart();

  const addToCart = () => {
    const product = { title, price, imageUrl };

    // Check if the product already exists in the cart
    const existingItem = state.items.find(item => item.title === product.title);

    if (existingItem) {
      // If exists, dispatch action to increment quantity
      dispatch({ type: 'ADD_TO_CART', payload: { id: existingItem._id } });
    } else {
      // If doesn't exist, dispatch action to add with quantity 1
      dispatch({ type: 'ADD_TO_CART', payload: { ...product, quantity: 1 } });
    }
  };

  return (
    <div className="product">
      <img src={imageUrl} alt={title} className="product-img" />
      <div className="product-title">{title}</div>
      <div className="product-price">${price}</div>
      <button className="add-to-cart-button" onClick={addToCart}>Add to Cart</button>
    </div>
  );
};

export default Product;
